export const template =
    {
        "type": "Batch-Update",
        "variables": {
            "translations": {
                "titles": {
                    "labelDescription": "Description",
                    "labelNotes": "Notes",
                    "labelRemoveBatchItem": "Remove batch item"
                }
            }
        },
        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "isVisible"    
                    },
                    {
                        "name": "batchValues",
                        "collection": true,
                        "datasource": 0
                    },
                    {
                        "name": "fieldOptions",
                        "collection": true,
                        "datasource": 1
                    }
                ]
            }
        ],
        "datasources": [
            {
                "id": 0,
                "name": "values",
                "resource": [
                    {
                        "id": 0,
                        "field": 0,
                        "value": "Test",
                        "type": "text"
                    },
                    {
                        "id": 1,
                        "field": 1,
                        "value": "123",
                        "type": "number"
                    }
                ]
            },
            {
                "id": 1,
                "name": "fields",
                "resource": [
                    {
                        "id": 0,
                        "title": "Notes",
                        "type": "text",
                        "default": true
                    },
                    {
                        "id": 1,
                        "title": "AutoResetValue",
                        "type": "number",
                        "default": true
                    }
                ]
            }
        ],
        "actions": [
            {
                "id": 0,
                "action": "context.addBatchItem"
            },
            {
                "id": 1,
                "action": "context.removeBatchItem"
            }
        ],
        "body": {
            "elements": [
                {
                    "element": "group",
                    "title": "Nested Repeats",
                    "elements": [
                        {
                            "element": "conditional-container",
                            "attributes": {
                                "visible.bind": "model.isVisible == true"
                            }
                        },
                        {
                            "element": "conditional-container",
                            "attributes": {
                                "visible.bind": "model.isVisible != true"
                            }
                        },
                        {
                            "element": "conditional-container",
                            "attributes": {
                                "visible.bind": "model.isVisible != null && model.isVisible == true"
                            }
                        },
                        {
                            "element": "html-template",
                            "elements": [
                                {
                                    "element": "div",
                                    "elements": [
                                        {
                                            "element": "select",
                                            "title": "title",
                                            "field": "field",
                                            "datasource": "model.fieldOptions"
                                        },
                                        {
                                            "element": "input",
                                            "title": "value",
                                            "field": "value",
                                            "attributes": {
                                                "type": "type"
                                            }
                                        },
                                        {
                                            "element": "button",
                                            "title": "Remove Item",
                                            "action": 1,
                                            "attributes": {
                                                "data-id": "#id"
                                            }
                                        }
                                    ],
                                    "styles": "batch-update-container"
                                }
                            ],
                            "attributes": {
                                "repeat": "model.batchValues"
                            }
                        }
                    ]
                },
                {
                    "element": "button",
                    "title": "Add Item",
                    "action": 0
                }
            ]
        },
        "resource": "Meter"
    };