export const template =
{
    "type": "view",
    "variables": {
        "titles": {
            "heading": "Complete Example",
            "header": "Header",
            "code": "Code",
            "description": "Description",
            "list": "List",
            "overview": "Overview",
            "graphics": "Graphics",
            "details": "Details",
            "parent": "Parent",
            "models": "Models",
            "makes": "Makes",
            "siteCode": "Site"
        },
        "ui": {
            "isMasterVisible": true
        }
    },
    "perspectives": [
        {
            "id": 0,
            "id-field": "id",
            "data": {
                "cache": "detail-lookup",
                "aggregate": "count",
                "sorting": {
                    "code": "ascending"
                }
            }
        }
    ],
    "datasets": [
        {
            "id": 0,
            "name": "model",
            "fields": [
                {
                    "name": "details",
                    "collection": true,
                    "datasource": 0
                }
            ]
        }
    ],
    "datasources": [
        {
            "id": 0,
            "remote": "details"
        }
    ],
    "body": {
        "elements": [
            {
                "element": "pragma-grid",
                "elements": [
                    {
                        "element": "pragma-grid-column",
                        "attributes": {
                            "field": "code",
                            "title": "@titles.code",
                            "data-width": 100
                        }
                    },
                    {
                        "element": "pragma-grid-column",
                        "attributes": {
                            "field": "description",
                            "title": "@titles.description",
                            "data-width": 100
                        }
                    }
                ],
                "attributes": {
                    "datasource.bind": "model.details",
                    "selection": "single",
                    "selectedId.bind": "model.selectedId",
                    "id-field": "id"
                }
            }
        ]
    }
};