export const template =
    {
        "type": "view",
        "variables": {
            "titles": {
                "list": "List",
                "grid": "Grid",
                "tree": "Tree",
                "code": "Code",
                "description": "Description",

            }
        },
        "perspectives": [
            {
                "id": 0,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "grouping": ["region", "siteCode"],
                    "sorting": {
                        "code": "ascending"
                    }
                }
            },
            {
                "id": 1,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "sorting": {
                        "code": "ascending"
                    }
                }
            }
        ],
        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "listItems",
                        "collection": true,
                        "datasource": 0
                    }
                ]
            }
        ],
        "datasources": [
            {
                "id": 0,
                "remote": "list-items",
                "perspective": {
                    "cache": "MeterDashboardCache",
                    "perspectives": [
                        0
                    ]
                },
            },
        ],
        "actions": [],
        "templates": [
            {
                "id": -1,
                "name": "Grid Template",
                "elements": [
                    {
                        "element": "grid",
                        "id": "grdWorkOrders",
                        "datasource": "model.listItems",
                        "perspective": 0,
                        "selectedId": "model.selectedId",
                        "formatters": [
                            {
                                "condition": "item.model.isActive == false",
                                "properties": {
                                    "color": "gray"
                                }
                            }
                        ],
                        "columns": [
                            {
                                "field": "model.code",
                                "title": "@titles.code",
                                "width": "100"
                            },
                            {
                                "field": "model.description",
                                "title": "@titles.description",
                                "width": "200",
                                "formatters": [
                                    {
                                        "properties": {
                                            "background": "cornflowerblue",
                                            "color": "white"
                                        }
                                    },
                                    {
                                        "condition": "item.model.id < 10",
                                        "properties": {
                                            "background": "red",
                                            "color": "silver"
                                        }
                                    }
                                ]
                            }
                        ],
                    }
                ]
            },
            {
                "id": 100,
                "name": "list items template",
                "elements": [
                    {
                        "element": "div",
                        "content": "#item.code"
                    },
                    {
                        "element": "div",
                        "content": "#item.description"
                    }
                ]
            },

        ],
        "body": {
            "elements": [
                {

                    "element": "div",
                    "styles": ["stretch"],
                    "title": "@titles.list",
                    "elements": [
                        {
                            "element": "list",
                            "id": "test",
                            "datasource": "model.listItems",
                            "selection": "multiple",
                            "selectedId": "model.selectedId",
                            "id-field": "id",
                            "perspective": "0",
                            "title-ref": "model.title",
                        },
                    ],
                    "actions": []
                }
            ]
        }
    };