export const template =
    {
        "type": "view",
        "variables": {
            "titles": {
                "list": "List",
                "grid": "Grid",
                "tree": "Tree",
                "code": "Code",
                "description": "Description",

            }
        },
        "perspectives": [
            {
                "id": 0,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "grouping": ["region", "siteCode"],
                    "sorting": {
                        "code": "ascending"
                    }
                }
            },
            {
                "id": 1,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "sorting": {
                        "code": "ascending"
                    }
                }
            }
        ],
        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "listItems",
                        "collection": true,
                        "datasource": 0
                    }
                ]
            }
        ],
        "datasources": [
            {
                "id": 0,
                "remote": "list-items",
                "hierarchical": true,
                "perspective": {
                    "cache": "MeterDashboardCache",
                    "perspectives": [
                        0
                    ]
                },
            },
        ],
        "actions": [],
        "templates": [
            {
                "id": 100,
                "name": "list items template",
                "elements": [
                    {
                        "element": "div",
                        "content": "#item.code"
                    },
                    {
                        "element": "div",
                        "content": "#item.description"
                    }
                ]
            },
            {
                "id": 1,
                "attributes": {
                    "condition": "model.type == 'folder'",
                },
                "elements": [
                    {
                        "element": "div",
                        "styles": ["flex"],
                        "elements": [
                            {
                                "element": "div",
                                "styles": ["flex"],
                                "elements": [{
                                    "element": "span",
                                    "content": "["
                                },
                                    {
                                        "element": "span",
                                        "styles": "bold",
                                        "content": "#model.id"
                                    },
                                    {
                                        "element": "span",
                                        "content": "] "
                                    },
                                    {
                                        "element": "span",
                                        "content": "#model.description"
                                    },
                                ]
                            }
                        ]
                    }
                ]

            },
            {
                "id": 2,
                "attributes": {
                    "condition": "model.type == 'asset'",
                },
                "elements": [
                    {
                        "element": "div",
                        "styles": ["flex"],
                        "elements": [
                            {
                                "element": "div",
                                "styles": ["stretch"],
                                "elements": [
                                    {
                                        "element": "div",
                                        "styles" : "bold",
                                        "content": "#model.id"
                                    },
                                    {
                                        "element": "div",
                                        "content": "#model.description"
                                    },
                                ]
                            }
                        ]
                    },
                ]
            },
            {
                "id": 3,
                "attributes": {
                    "condition": "model.type == 'component'",
                },
                "elements": [
                    {
                        "element": "div",
                        "styles": ["flex"],
                        "elements": [
                            {
                                "element": "div",
                                "styles": ["stretch"],
                                "elements": [
                                    {
                                        "element": "div",
                                        "content": "#model.id"
                                    },
                                    {
                                        "element": "div",
                                        "content": "#model.description"
                                    },
                                    {
                                        "element": "div",
                                        "styles": ["flex"],
                                        "elements": [
                                            {
                                                "element" : "span",
                                                "styles": "bold",
                                                "content": "Failures: "
                                            },
                                            {
                                                "element" : "span",
                                                "content": "#model.failureCount"
                                            }
                                        ]
                                    },
                                ]
                            }
                        ]
                    },
                ]
            },
            {
                "id": 4,
                "attributes": {
                    "condition": "model.type == 'task'",
                },
                "elements": [
                    {
                        "element": "div",
                        "elements": [  {
                            "element": "div",
                            "content": "#model.description"
                        },
                            {
                                "element": "div",
                                "styles": "flex",
                                "elements": [
                                    {
                                        "element": "span",
                                        "styles": "bold",
                                        "content": "Data Created"
                                    },
                                    {
                                        "element": "span",
                                        "content": ":"
                                    },
                                    {
                                        "element": "span",
                                        "content": "#model.dateCreated"
                                    }]
                            },]
                    },


                ]
            },
            {
                "id": 5,
                "attributes": {
                    "condition": "model.type == 'assetFolder'",
                },
                "elements": [
                    {
                        "element": "div",
                        "styles": ["flex"],
                        "elements": [
                            {
                                "element": "div",
                                "elements": [{
                                    "element": "div",
                                    "content": "#model.description"
                                },
                                    {
                                        "element": "div",
                                        "styles": ["bold", "flex"],
                                        "elements": [
                                            {
                                                "element": "span",
                                                "content": "Count: "
                                            },
                                            {
                                                "element": "span",
                                                "content": "#model.count"
                                            },
                                        ]
                                    },

                                ]
                            }
                        ]
                    }
                ]
            }

        ],
        "body": {
            "elements": [
                {
                    "element": "div",
                    "styles": ["fullsize-flex"],
                    "elements": [
                        {

                            "element": "panel-bar",
                            "styles": ["stretch"],
                            "title": "@titles.list",
                            "elements": [
                                {
                                    "element": "list",
                                    "formatters" : [
                                        {
                                            "condition": "item.model.failureCount > 30 && item.model.failureCount < 40",
                                            "properties": {
                                                "color": "orange"
                                            }
                                        },
                                        {
                                            "condition": "item.model.failureCount > 40",
                                            "properties": {
                                                "background": "lightgrey",
                                                "color": "red",
                                                "font": "large",
                                            }
                                        },
                                        {
                                            "condition": "item.model.type == 'task'",
                                            "properties": {
                                                "color": "darkblue"
                                            }
                                        }
                                    ],
                                    "templates": [1,2,3,4,5],
                                        "datasource": "model.listItems",
                                        "selection": "multiple",
                                        "type-ref": "type",
                                        "title-ref": "model.description",

                                    "id" : "test"
                                },

                            ],
                            "actions": []
                        }
                    ]
                }

            ]
        }
    };