export const template =
    {
        "type": "view",
        "variables": {
            "titles": {
                "list": "List",
                "grid": "Grid",
                "tree": "Tree",
                "code": "Code",
                "description": "Description",

            }
        },
        "perspectives": [
            {
                "id": 0,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "grouping": ["region", "siteCode"],
                    "sorting": {
                        "code": "ascending"
                    }
                }
            },
            {
                "id": 1,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "sorting": {
                        "code": "ascending"
                    }
                }
            }
        ],
        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "listItems",
                        "collection": true,
                        "datasource": 0
                    }
                ]
            }
        ],
        "datasources": [
            {
                "id": 0,
                "remote": "list-items",
                "hierarchical": true,
                "perspective": {
                    "cache": "MeterDashboardCache",
                    "perspectives": [
                        0
                    ]
                },
            },
        ],
        "actions": [],
        "templates": [
            {
                "id": 100,
                "name": "list items template",
                "elements": [
                    {
                        "element": "div",
                        "content": "#item.code"
                    },
                    {
                        "element": "div",
                        "content": "#item.description"
                    }
                ]
            },

        ],
        "body": {
            "elements": [
                {
                    "element": "div",
                    "styles": ["fullsize-flex"],
                    "elements": [
                        {

                            "element": "panel-bar",
                            "styles": ["stretch"],
                            "title": "@titles.tree",
                            "elements": [
                                {
                                    "element": "pragma-advanced-tree",
                                    "elements": [
                                        {
                                            "element": "pragma-conditional-formatter",
                                            "attributes": {
                                                "condition": "item.model.failureCount > 30 && item.model.failureCount < 40",
                                                "style-property": "color",
                                                "value": "orange",
                                            }
                                        },
                                        {
                                            "element": "pragma-conditional-formatter",
                                            "attributes": {
                                                "condition": "item.model.failureCount > 40",
                                                "style-property": "background, color, font",
                                                "value": "lightgrey, red, large",
                                            }
                                        },
                                        {
                                            "element": "pragma-conditional-formatter",
                                            "attributes": {
                                                "condition": "item.model.type == 'task'",
                                                "style-property": "color",
                                                "value": "darkblue",
                                            }
                                        },
                                        {
                                            "element": "html-template",
                                            "attributes": {
                                                "condition": "model.type == 'folder'",
                                            },
                                            "elements": [
                                                {
                                                    "element": "div",
                                                    "styles": ["flex"],
                                                    "elements": [
                                                        {
                                                            "element": "div",
                                                            "styles": ["flex"],
                                                            "content": "<svg width=\"32\" height=\"32\" xmlns=\"http://www.w3.org/2000/svg\"><use xlink:href=\"#folder\"></use></svg>"

                                                        },
                                                        {
                                                            "element": "div",
                                                            "styles": ["flex"],
                                                            "elements": [{
                                                                "element": "span",
                                                                "content": "["
                                                            },
                                                                {
                                                                    "element": "span",
                                                                    "styles": "bold",
                                                                    "content": "#model.id"
                                                                },
                                                                {
                                                                    "element": "span",
                                                                    "content": "] "
                                                                },
                                                                {
                                                                    "element": "span",
                                                                    "content": "#model.description"
                                                                },
                                                            ]
                                                        }
                                                    ]
                                                }
                                            ]
                                        },
                                        {
                                            "element": "html-template",
                                            "attributes": {
                                                "condition": "model.type == 'assetFolder'",
                                            },
                                            "elements": [
                                                {
                                                    "element": "div",
                                                    "styles": ["flex"],
                                                    "elements": [
                                                        {
                                                            "element": "div",
                                                            "styles": ["flex"],
                                                            "content": "<svg width=\"32\" height=\"32\" xmlns=\"http://www.w3.org/2000/svg\"><use xlink:href=\"#folder\"></use></svg>"

                                                        },
                                                        {
                                                            "element": "div",
                                                            "elements": [{
                                                                "element": "div",
                                                                "content": "#model.description"
                                                            },
                                                                {
                                                                    "element": "div",
                                                                    "styles": ["bold", "flex"],
                                                                    "elements": [
                                                                        {
                                                                            "element": "span",
                                                                            "content": "Count: "
                                                                        },
                                                                        {
                                                                            "element": "span",
                                                                            "content": "#model.count"
                                                                        },
                                                                    ]
                                                                },

                                                            ]
                                                        }
                                                    ]
                                                }
                                            ]
                                        },
                                        {
                                            "element": "html-template",
                                            "attributes": {
                                                "condition": "model.type == 'component'",
                                            },
                                            "elements": [
                                                {
                                                    "element": "div",
                                                    "styles": ["flex"],
                                                    "elements": [
                                                        {
                                                            "element": "div",
                                                            "content": "<svg width=\"32\" height=\"32\" xmlns=\"http://www.w3.org/2000/svg\"><use xlink:href=\"#component\"></use></svg>"

                                                        },
                                                        {
                                                            "element": "div",
                                                            "styles": ["stretch"],
                                                            "elements": [
                                                                {
                                                                    "element": "div",
                                                                    "content": "#model.id"
                                                                },
                                                                {
                                                                    "element": "div",
                                                                    "content": "#model.description"
                                                                },
                                                                {
                                                                    "element": "div",
                                                                    "styles": ["flex"],
                                                                    "elements": [
                                                                        {
                                                                            "element" : "span",
                                                                            "styles": "bold",
                                                                            "content": "Failures: "
                                                                        },
                                                                        {
                                                                            "element" : "span",
                                                                            "content": "#model.failureCount"
                                                                        }
                                                                    ]
                                                                },
                                                            ]
                                                        }
                                                    ]
                                                },
                                            ]
                                        },
                                        {
                                            "element": "html-template",
                                            "attributes": {
                                                "condition": "model.type == 'asset'",
                                            },
                                            "elements": [
                                                {
                                                    "element": "div",
                                                    "styles": ["flex"],
                                                    "elements": [
                                                        {
                                                            "element": "div",
                                                            "content": "<svg width=\"32\" height=\"32\" xmlns=\"http://www.w3.org/2000/svg\"><use xlink:href=\"#asset\"></use></svg>"

                                                        },
                                                        {
                                                            "element": "div",
                                                            "styles": ["stretch"],
                                                            "elements": [
                                                                {
                                                                    "element": "div",
                                                                    "styles" : "bold",
                                                                    "content": "#model.id"
                                                                },
                                                                {
                                                                    "element": "div",
                                                                    "content": "#model.description"
                                                                },
                                                            ]
                                                        }
                                                    ]
                                                },
                                            ]
                                        },
                                        {
                                            "element": "html-template",
                                            "attributes": {
                                                "condition": "model.type == 'task'",
                                            },
                                            "elements": [
                                                {
                                                    "element": "div",
                                                    "elements": [  {
                                                        "element": "div",
                                                        "content": "#model.description"
                                                    },
                                                      ]
                                                },


                                            ]
                                        }
                                    ],
                                    "attributes": {
                                        "datasource.bind": "model.listItems",
                                        "selection": "multiple",
                                        "selected-id.bind": "model.selectedId",
                                        "id-field": "id",
                                        "type-ref": "type"
                                    }
                                },

                            ],
                            "actions": []
                        }
                    ]
                }

            ]
        }
    };