import {ViewBase} from "../../../baremetal/lib/view-base.js";

export default class BarCodeScanning extends ViewBase {
    connectedCallback(element) {
        this._element = element;
        this.title = "Barcode Scanning";
        this.context = this;
        this.start();
    }

    disconnectedCallback() {
    }

    start() {
        window.eventEmitter.emit("display-barcode-scanner");
    }
}