import {ViewBase} from "./../../../baremetal/lib/view-base.js";
import {addExpressionTriggers} from './../../../baremetal/lib/binding/observers.js';

export default class BindingExpressions extends ViewBase {
    connectedCallback() {
        this.data = {
            value: 0
        };

        addExpressionTriggers(this, "data.field1 === 0", () => console.log("value is greater than zero"));
    }

    performAction() {
        this.data = {
            field1: 0,
            value: 10
        };
    }
}