import {ViewBase} from "./../../../baremetal/lib/view-base.js";

export default class BindingTests extends ViewBase {
    connectedCallback() {
        this.model = {
            name: "model",
            data: {
                field1: "field1",
                field2: "field2"
            }
        }
    }
    
    changeModel() {
        this.model = {
            name: "new model",
            data: {
                field1: "new field1",
                field2: "new field2"
            }
        }
    }
    
    changeData() {
        this.model.data = {
            field1: "data change field1",
            field2: "data change field2"
        }
    }   
    
    changeFields() {
        this.model.data.field1 = "Hello Field1";
        this.model.data.field2 = "Hello FIeld2";
        
        this.model.data.subData = {
            code: "AB123"
        }
    }
}