import {ViewBase} from "./../../../baremetal/lib/view-base.js";
import {BreadcrumbNavigationMediator} from "./../../../webcomponents/pragma-breadcrumb/breadcrumb-navigation-mediator.js";
export default class BreadcrumbTests extends ViewBase {
    connectedCallback() {
      
        

        this.breadcrumb = document.querySelector("pragma-breadcrumb");
        this.breadcrumb.mediator = new BreadcrumbNavigationMediator();
    }
    
    replace() {
        
        this.breadcrumb.replaceLast({
            remote: "Replaced",
            context: "0987"
        })
    }

    navigate() {

        window.eventEmitter.postMessage("pragma-navigator", {
            remote: "breadcrumb-tests",
            id: 1234,
            context: 1234
        })
    }
}