export const template =
{
    "variables": {
        "titles": {
            "selectFirst": "Select First",
            "showDialog": "Show Dialog",
            "options": "Options"
        },
        "data": {
            "selectedOption": 0,
            "viewId": 0,
            "value": "hello world"
        },
        "models": {
            "manualMeterReset": 1
        }
    },
    "customActionEvents": [
        {
            "event": "@models.manualMeterReset.field1 == 'test'",
            "actions": [
                {
                    "action": "setValue",
                    "parameters": {
                        "@models.manualMeterReset.field2": "Test Success"
                    }
                }
            ]
        },
        {
            "event": "@models.manualMeterReset.field1 == 'null'",
            "actions": [
                {
                    "action": "setValue",
                    "parameters": {
                        "@models.manualMeterReset.field2": "@null"
                    }
                }
            ]
        }
    ],
    "datasets": [
        {
            "id": 1,
            "fields": [
                {
                    "name": "field1",
                    "default": "Field 1"
                },
                {
                    "name": "field2",
                    "default": "Field 2"
                }
            ]
        }    
    ],
    "datasources": [
        {
            "id": 0,
            "resource": [
                {
                    "id": 0,
                    "title": "Option 1"
                },
                {
                    "id": 1,
                    "title": "Option 2"
                }
            ]
        }
    ],
    "processes": [
        {
            "id": 1,
            "actions": [4, -1]
        }  
    ],
    "actions": [
        {
            "id": -1,
            "action": "closeDialog"
        },
        {
            "id": 0,
            "action": "select-first",
            "remote": "none",
            "parameters": {
                "activity-id": 1000,
                "refresh-model": true
            }
        },
        {
            "id": 1,
            "remote": "none",
            "title": "@titles.showDialog",
            "template": 0,
            "show-accept": false,
            "parameters": {
                "activity-id": 1001
            }
        },
        {
            "id": 2,
            "remote": "none",
            "template": 1,
            "parameters": {
                "activity-id": 1002
            }
        },
        {
            "id": 3,
            "action": "setVariable",
            "parameters": {
                "name": "data.viewId",
                "value": 1
            }
        },
        {
            "id": 4,
            "action": "setVariable",
            "parameters": {
                "name": "data.viewId",
                "value": 0
            }
        },
        {
            "id": 5,
            "action": "emit",
            "parameters": {
                "key": "show-message",
                "type": 0,
                "message": {
                    "message": "Hello World"
                },
                "activity-id": 1003
            },
            "conditional": [
                {
                    "expression": "model.detailSelectedId.length == 1",
                    "message": "Please select a single item fro this to work"
                }
            ]
        },
        {
            "id": 6,
            "action": "postMessage",
            "parameters": {
                "query": "pragma-navigator",
                "view": "repeat-ui",
                "remote": "testRemote",
                "id": 1001,
                "activity-id": 1004
            }
        }
    ],
    "templates": [
        {
            "id": 0,
            "elements": [
                {
                    "element": "input",
                    "title": "Some Input Field",
                    "field": "model.selectedId"
                },
                {
                    "element": "input",
                    "title": "Code",
                    "field": "model.data.code"
                },
                {
                    "element": "input",
                    "title": "Field 1",
                    "field": "@models.manualMeterReset.field1"
                },
                {
                    "element": "input",
                    "title": "Field 2",
                    "field": "@models.manualMeterReset.field2"
                }
            ]
        },
        {
            "id": 1,
            "elements": [
                {
                    "element": "group",
                    "title": "Options",
                    "elements": [
                        {
                            "element": "select",
                            "title": "@titles.options",
                            "datasource": 0,
                            "field": "@data.selectedOption"
                        },
                        {
                            "element": "input",
                            "title": "View Id",
                            "field": "@data.viewId"
                        }
                    ]
                },
                {
                    "element": "group",
                    "title": "Details",
                    "elements": [
                        {
                            "element": "conditional-container",
                            "elements": [
                                {
                                    "element": "div",
                                    "content": "Option 1 Stuff"
                                }
                            ],
                            "attributes": {
                                "visible.bind": "@data.selectedOption == 0"
                            }
                        },
                        {
                            "element": "conditional-container",
                            "elements": [
                                {
                                    "element": "conditional-container",
                                    "elements": [
                                        {
                                            "element": "div",
                                            "content": "condition: @data.viewId == 0"
                                        },
                                        {
                                            "element": "hr"
                                        },
                                        {
                                            "element": "input",
                                            "title": "Value",
                                            "field": "@data.value"
                                        },
                                        {
                                            "element": "button",
                                            "title": "Get Value",
                                            "action": 3
                                        }
                                    ],
                                    "attributes": {
                                        "visible.bind": "@data.viewId == 0"
                                    }
                                },
                                {
                                    "element": "conditional-container",
                                    "elements": [
                                        {
                                            "element": "div",
                                            "content": "condition: @data.viewId == 1"
                                        },
                                        {
                                            "element": "hr"
                                        },
                                        {
                                            "element": "input",
                                            "title": "Please provide a new Value",
                                            "field": "@data.value"
                                        },
                                        {
                                            "element": "button",
                                            "title": "OK",
                                            "process": 1
                                        }
                                    ],
                                    "attributes": {
                                        "visible.bind": "@data.viewId == 1"
                                    }
                                }
                            ],
                            "attributes": {
                                "visible.bind": "@data.selectedOption == 1"
                            }
                        }
                    ]
                }
            ]
        }
    ],
    "body": {
        "elements": [
            {
                "element": "action-button",
                "title": "@titles.selectFirst",
                "action": 0
            },
            {
                "element": "action-button",
                "title": "@titles.showDialog",
                "action": 1
            },
            {
                "element": "action-button",
                "title": "Complex",
                "action": 2
            },
            {
                "element": "action-button",
                "title": "Event Emitter",
                "action": 5
            },
            {
                "element": "action-button",
                "title": "Repeat-UI",
                "action": 6
            }
        ]
    }
};