export const template = {
    "variables": {
        "labels": {
            "isCalculated": "Is Calculated",
            "value": "Value"
        }
    },
    "actions": [
        {
            "id": 0,
            "action": "context.debug"
        }
    ],
    "datasets": [
        {
            "id": 0,
            "fields": [
                {
                    "name": "id"
                },
                {
                    "name": "isCalculated"
                },
                {
                    "name": "value"
                }
            ]
        }    
    ],
    "customActionEvents": [
        {
            "event": "model.isCalculated == true",
            "actions": [
                {
                    "action": "setValidation",
                    "parameters": {
                        "model.value": ["required", "minlength = 5", "maxlength = 10"]
                    }
                }
            ]
        },
        {
            "event": "model.isCalculated == false",
            "actions": [
                {
                    "action": "setValue",
                    "parameters": {
                        "model.value": "@null"
                    }
                }
            ]
        }
    ],
    "body": [
        {
            "elements": [
                {
                    "element": "h2",
                    "content": "Custom Validation"
                },
                {
                    "element": "p",
                    "content": "if is calculated is checked, you must provide a value, but if not set the value to null"
                },
                {
                    "element": "checkbox",
                    "title": "@labels.isCalculated",
                    "field": "model.isCalculated"
                },
                {
                    "element": "input",
                    "title": "@labels.value",
                    "field": "model.value"
                },
                {
                    "element": "button",
                    "title":"Debug",
                    "action": 0
                }
            ]
        }
    ]
};