export const template = {
    "variables": {
        
    },
    "datasets": [
        {
            "id": 0,
            "name": "model",
            "fields": [
                {
                    "name": "id",
                },
                {
                    "name": "firstName",
                    "default": "Joe"
                },
                {
                    "name": "lastName",
                    "default": "Dirt"
                },
                {
                    "name": "age",
                    "default": 30
                },
                {
                    "name": "local",
                    "default": false
                },
                {
                    "name": "parameters",
                    "dataset": 1
                },
                {
                    "name": "meterreading",
                    "dataset": 2
                },
            ],
            "primaryActions": {
                "update": {
                    "Meter->Id": "@routeParameters.meterId",
                    "MeterReading->Id": "model.id"
                }
            }
        },
        {
            "id": 1,
            "name": "parameters",
            "fields": [
                {
                    "name": "meterId"
                }
            ]
        },
        {
            "id": 2,
            "name": "meterreading",
            "fields": [
                {
                    "name": "id",
                },
                {
                    "name": "reading",
                    "default": "Joe"
                },
            ],
            "primaryActions": {
                "update": {
                    "Meter->Id": "model.id",
                    "MeterReading->Id": "model.id"
                }
            }
        }
    ],
    "actions": [
        {
            "id": 0,
            "action": "context.increaseAge"
        }
    ],
    "body": {
        "elements": [
            {
                "element": "group",
                "title": "Main",
                "elements": [


                    {
                        "element": "h2",
                        "content": "${model.firstName} ${model.lastName} is ${model.age} old"
                    },
                    {
                        "element": "input",
                        "title": "First Name",
                        "field": "model.firstName"
                    },
                    {
                        "element": "input",
                        "title": "Last Name",
                        "field": "model.lastName"
                    },
                    {
                        "element": "input",
                        "title": "Age",
                        "field": "model.age"
                    },
                    {
                        "element": "checkbox",
                        "title": "Local",
                        "field": "model.local"
                    },
                    {
                        "element": "button",
                        "title": "Increase Age",
                        "action": 0
                    }
                ]
            },
            {
                "element": "group",
                "title": "Reading",
                "elements": [
                    {
                        "element": "input",
                        "title": "Id",
                        "field": "model.meterreading.id"
                    },
                    {
                        "element": "checkbox",
                        "title": "Reading",
                        "field": "model.meterreading.reading"
                    }
                ]
            }
        ]
    }
}