import {ViewBase} from "./../../../baremetal/lib/view-base.js";

export default class Designer extends ViewBase {
    connectedCallback() {
        this.viewContainer = this._element.querySelector("view-container");
        this.views = ["designer-a", "designer-b"];
    }
    
    disconnectedCallback() {
        this.viewContainer = null;
    }
    
    showForm(index) {
        this.viewContainer.showView(this.views[index]);
    }
}