export const template = {
    "datasets": [
        {
            "id": 0,
            "name": "model",
            "fields": [
                {
                    "name": "firstName",
                    "default": "Joe",
                    "validations": {
                        "maxLength": {
                            "origin": "domain",
                            "value": 50
                        },
                        "minLength": {
                            "origin": "domain",
                            "value": 1
                        },
                        "null": {
                            "origin": "domain",
                            "value": false
                        },
                        "required": {
                            "value": true
                        }
                    }
                },
                {
                    "name": "lastName",
                    "default": "Dirt"
                },
                {
                    "name": "age",
                    "default": 30
                },
                {
                    "name": "local",
                    "default": false
                }
            ]
        }  
    ],
    "actions": [
        {
            "id": 0,
            "action": "context.increaseAge"
        }
    ],
    "body": {
        "elements": [
            {
                "element": "h2",
                "content": "${model.firstName} ${model.lastName} is ${model.age} old"
            },
            {
                "element": "input",
                "title": "First Name",
                "field": "model.firstName"
            },
            {
                "element": "input",
                "title": "Last Name",
                "field": "model.lastName"
            },
            {
                "element": "input",
                "title": "Age",
                "field": "model.age"
            },
            {
                "element": "checkbox",
                "title": "Local",
                "field": "model.local"
            },
            {
                "element": "button",
                "title": "Increase Age",
                "action": 0
            }
        ]
    }
}