export const template = {
    "datasets": [
        {
            "id": 0,
            "fields": [
                {
                    "name": "id"
                },
                {
                    "name": "items",
                    "collection": true,
                    "datasource": 0
                }
            ]
        }  
    ],
    "datasources": [
        {
            "id": 0,
            "remote": "items"
        }    
    ],
    "actions": [
        {
            "id": 0,
            "action": "context.startMonitoring"
        }  
    ],
    "body":
    {
        "elements": [
            {
                "element": "button",
                "title": "Start",
                "action": 0
            },
            {
                "element": "div",
                "elements": [
                    {
                        "element": "html-template",
                        "attributes": {
                            "repeat": "model.items"
                        },
                        "elements": [
                            {
                                "element": "div",
                                "styles": ["card", "default-padding"],
                                "elements": [
                                    {
                                        "element": "div",
                                        "content": "${code}"
                                    }
                                ]
                            }
                        ]
                    }
                ]
            }
        ]
    }
}
