import {ViewBase} from "../../../baremetal/lib/view-base.js";

export default class Imaging extends ViewBase {
    connectedCallback(element) {
        this._element = element;
        this.img = element.querySelector("img");
    }
    
    disconnectedCallback() {
        this._element = null;
    }

    captured(event) {
        this.img.src = event.detail
    }
    
    start() {
        this._element.querySelector("pragma-imagecapture").startCapturing();
    }
    
    stop() {
        this._element.querySelector("pragma-imagecapture").stopCapturing();
    }
}