export const template = {
    "actions": [
        {
            "id": 0,
            "action": "context.log"
        }
    ],
    
    "datasets": [
        {
            "id": 0,
            "name": "model",
            "fields": [
                {
                    "name": "columns",
                    "collection": true,
                    "datasource": 0
                },
                {
                    "name": "options",
                    "collection": true,
                    "datasource": 1
                },
                {
                    "name": "option"
                },
                {
                    "name": "field1",
                    "default": "Hello"
                },
                {
                    "name": "field2",
                    "default": "World"
                }
            ]
        }  
    ],
    
    "datasources": [
        {
            "id": 0,
            "name": "columns",
            "remote": "getColumns"
        },
        {
            "id": 1,
            "name": "options",
            "remote": "getOptions"
        }
    ],
    
    "body": {
        "elements": [
            {
                "element": "div",
                "styles": ["toolbar"],
                "elements": [
                    {
                        "element": "button",
                        "title": "Print",
                        "action": 0
                    }
                ]
            },
            {
                "element": "group",
                "title": "columns",
                "elements": [
                    {
                        "element": "html-template",
                        "attributes": {
                            "repeat": "model.columns"
                        },
                        "elements": [
                            {
                                "element": "div",
                                "content": "Id: ${id} ${title} woot!"
                            },
                            {
                                "element": "input",
                                "title" : "Title",
                                "field" : "title"
                            }
                        ]
                    }
                ]
            },
            {
                "element": "group",
                "title": "Comboboxes",
                "elements": [
                    {
                        "element": "select",
                        "title": "Options",
                        "field": "model.option",
                        "datasource": "model.options"
                    },
                    {
                        "element": "input",
                        "title": "Options Value",
                        "field": "model.option"
                    }
                ]
            }
        ]
    }
}