export const template = {
    "variables": {
        "data": {
            "field1": "Field 1",
            "field2": 2,
            "field3": "Will chane on field 1 change"
        }
    },
    "actions": [
        {
            "id": 0,
            "action": "context.debug"
        }
    ],
    "customActionTriggers": [
        {
            "trigger": "@data.field1",
            "actions": [
                {
                    "action": "setValue",
                    "condition": "isMobile == true",
                    "parameters": {
                        "@data.field3": "*** triggered by field1 changing ***"
                    }
                }
            ]
        }
    ],
    "customActionEvents": [
        {
            "event": "@data.field2 == 6",
            "actions": [
                {
                    "action": "setValue",
                    "parameters": {
                        "@data.field1": "Dude!!"
                    }
                }
            ]
        }
    ],
    "body": [
        {
            "elements": [
                {
                    "element": "h2",
                    "content": "Johan sais: ${@data.field1} - ${@data.field2 * 2}"
                },
                {
                    "element": "input",
                    "title": "Variable 1",
                    "field": "@data.field1"
                },
                {
                    "element": "input",
                    "title": "Variable 3",
                    "field": "@data.field3"
                },
                {
                    "element": "button",
                    "title":"Debug",
                    "action": 0
                }
            ]
        }
    ]
};