export const template =
    {
        "resource": "site",
        "type": "view",
        "variables": {
            "translations": {
                "contextMenuTitle": "My context menu"
            },
            "datasources": {
                "firstContextMenu": 1,
                "secondContextMenu": 2,
                "details": 3,
            },
        },

        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "selectedId",
                        "default": []
                    }
                ]
            },
        ],
        "datasources": [
            {
                "id": 3,
                "remote": "list-items",
                "resource": [
                    {
                        "id": 0,
                        "code": "A",
                        "description": "A Description",
                        "isActive": true
                    },
                    {
                        "id": 1,
                        "code": "B",
                        "description": "B Description",
                        "isActive": true
                    },
                    {
                        "id": 2,
                        "code": "C",
                        "description": "C Description",
                        "isActive": true
                    }
                ]
            },
            {
                "id": 1,
                "hierarchical": true,
                "resource": [
                    {
                        "title": "Menu item 1",
                        "id": 1
                    },
                    {
                        "title": "Menu item 2",
                        "id": 2
                    },
                    {
                        "title": "Menu item 3",
                        "id": 3
                    }
                ]
            },
            {
                "id": 2,
                "hierarchical": true,
                "resource": [
                    {
                        "title": "Secondary menu item 1",
                        "id": 1
                    },
                    {
                        "title": "Secondary menu item 2",
                        "id": 2
                    },
                    {
                        "title": "Secondary menu item 3",
                        "id": 3
                    }
                ]
            }
        ],
        "templates": [
            {
                "id": 100,
                "name": "list items template",
                "elements": [
                    {
                        "element": "div",
                        "content": "#code"
                    },
                    {
                        "element": "div",
                        "content": "#description"
                    }
                ]
            }
        ],
        "uiRules": [
            {
                "id": 1,
                "rules": [
                    {
                        "device": "tablet | desktop"
                    },
                    {
                        "device": "phone",
                        "orientation": "landscape"
                    }
                ]
            },
            {
                "id": 2,
                "rules": [
                    {
                        "device": "phone",
                        "orientation": "portrait"
                    }
                ]
            }
        ],
        "contextMenus": [
            {
                "id": 1,
                "exclude": [
                    "[role='columnheader']", "[role='table']"
                ],
                "title": "@translations.contextMenuTitle",
                "items":[
                    {
                        condition: "@datasources.details.selectedItems[0].code == 'A'",
                        datasource: "@datasources.firstContextMenu"
                    },
                    {
                        condition: "@datasources.details.selectedItems[0].code == 'B'",
                        datasource: "@datasources.secondContextMenu"
                    }
                ]
            },
            {
              "id": 2,
              "datasource": "@datasources.test"
            }
        ],
        "actions": [
            {
                "id": 1,
                "action": "showContextMenu",
                "parameters" : {
                    "contextMenu": 1
                }
            }
        ],
        "body": {
            "elements": [
                {
                    "element": "panel-bar",
                    "title": "UI Rules",
                    "styles": "fullsize-flex",
                    "actions": [
                        {
                            "element": "button",
                            "title": "Test",
                            "action": 1
                        }
                    ],
                    "elements": [
                        {
                            "element": "grid",
                            "uiRule": 1,
                            "contextMenu": 1,
                            "columns": [{
                                "field": "code",
                                "title": "Code",
                                "data-width": 100
                            },
                                {
                                    "field": "description",
                                    "title": "Description",
                                    "data-width": 100
                                },
                                {
                                    "field": "isActive",
                                    "title": "Is Active",
                                    "data-width": 100
                                }],
                            "datasource": "@datasources.details",
                            "selectedId": "model.selectedId",
                            "selection": "single",
                            "id": "myGrid"
                        },
                        {
                            "element": "list",
                            "contextMenu": 1,
                            "datasource": "model.details",
                            "selectedId": "model.selectedId",
                            "selection": "single",
                            "templates": [100],
                            "id": "myList",
                            "uiRule": 2,
                        }
                    ]
                }
            ]
        }
    };