import {ViewBase} from "./../../../baremetal/lib/view-base.js";

export default class VisibleStateTracking extends ViewBase {

    connectedCallback() {

    }

    toggleSplitView() {
        window.eventEmitter.postMessage("#testsplit", {
            message: "toggle"
        });

        const test = document.querySelector("#testsplit");

        test.setAttribute("state", "suspended");
    }
}