export const template =
    {
        "type": "view",
        "variables": {
            "titles": {
                "list": "List",
                "grid": "Grid",
                "tree": "Tree",
                "code": "Code",
                "description": "Description",

            }
        },
        "perspectives": [
            {
                "id": 0,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "grouping": ["region"],
                    "sorting": {
                        "code": "ascending"
                    }
                }
            },
            {
                "id": 1,
                "id-field": "id",
                "data": {
                    "cache": "detail-lookup",
                    "aggregate": "count",
                    "sorting": {
                        "code": "ascending"
                    }
                }
            }
        ],
        "datasets": [
            {
                "id": 0,
                "name": "model",
                "fields": [
                    {
                        "name": "listItems",
                        "collection": true,
                        "datasource": 0
                    }
                ]
            }
        ],
        "datasources": [
            {
                "id": 0,
                "remote": "list-items",
                "perspective": {
                    "cache": "MeterDashboardCache",
                    "perspectives": [
                        0
                    ]
                },
            },
        ],
        "actions": [],
        "templates": [
            {
                "id": 100,
                "name": "list items template",
                "elements": [
                    {
                        "element": "div",
                        "content": "#item.code"
                    },
                    {
                        "element": "div",
                        "content": "#item.description"
                    }
                ]
            },

        ],
        "body": {
            "elements": [
                {
                    "element": "div",
                    "styles": ["fullsize-flex"],
                    "elements": [
                        {

                            "element": "panel-bar",
                            "styles": ["stretch"],
                            "title": "@titles.grid",
                            "elements": [
                                {
                                    "element": "pragma-filter",
                                    "attributes": {
                                        "datasource.bind": "model.listItems",
                                        "placeholder": "Filter"
                                    }
                                },
                                {
                                    "element": "pragma-advanced-grid",
                                    "elements": [

                                        {
                                            "element": "pragma-grid-column",
                                            "attributes": {
                                                "field": "model.code",
                                                "title": "@titles.code",
                                                "data-width": 200
                                            }
                                        },
                                        {
                                            "element": "pragma-grid-column",
                                            "attributes": {
                                                "field": "model.description",
                                                "title": "@titles.description",
                                                "data-width": 400
                                            }
                                        }
                                    ],
                                    "attributes": {
                                        "datasource.bind": "model.listItems",
                                        "selection": "multiple",
                                        "selected-id.bind": "model.selectedId",
                                        "id-field": "id",
                                        "perspective" : "0",
                                        "id": "advanced-grid"
                                    }
                                },
                                
                            ],
                            "actions": []
                        }
                    ]
                }

            ]
        }
    };