export function runTask(fn) {
    setTimeout(fn(), 0);
}

export function runMicroTask(fn) {
    Promise.resolve().then(fn());
}

export function runUITask(fn) {
    requestAnimationFrame(() => fn());
}

export function performAsyncFrame(fn) {
    return new Promise((resolve) => {
        requestAnimationFrame(() => {
            const result = fn();
            resolve(result);
        })
    })
}