import {IntentionHelper} from "../../lib/intention-helper.js";

export function addIntentionHelper(obj) {
    if (obj.intentionHelper  == null) {
        obj.intentionHelper = new IntentionHelper();
        obj.registerInputIntent = obj.intentionHelper.registerIntention.bind(obj.intentionHelper);
        obj.unRegisterIntention = obj.intentionHelper.unRegisterIntention.bind(obj.intentionHelper);
    } 
}

export function removeIntentionHelper(obj) {
    obj.intentionHelper.dispose();
    obj.intentionHelper = null;
}