const pragmaTesting = require("pragma-testing/lib/node/html-test-page-builder.js");

const builder = new pragmaTesting.HtmlTestPageBuilder();
builder.build(
    [
        "./bootstrap/boot-translations.js",
        "./bootstrap/boot-events.js",
        "./baremetal/lib/conveters/**/*.js",
        "./lib/**/*.js",
        "./webcomponents/**/*.js",
    ],
    [
        "./test/unit/baremetal/**/*.js",
        "./test/unit/lib/**/*.js",
        "./test/unit/webcomponents/**/*.js",
    ],
    sourcePath => sourcePath
        .replace("./bootstrap","./../../bootstrap")
        .replace("./baremetal","./../../baremetal")
        .replace("./lib","./../../lib")
        .replace("./webcomponents","./../../webcomponents"),
    testPath => testPath
        .replace("./test","./../../test"),
    "./test/unit/unit-tests-teamcity-generated.html",
    "./../../node_modules",
    "{ui: 'bdd', timeout: 120000, reporter: 'teamcity', globals: ['eventEmitter','groupWorker', 'dialogManager', 'addEventListener', 'removeEventListener', 'matchMedia']}"
);