export function createActionButton(title, activityId, classList = "primary") {
    const button  = document.createElement("pragma-action-button");
    button.title = title;
    button.setAttribute("activity-id",activityId);
    button.classList.add(classList);
    return button;
}

export function createButton(title, classList = "aux"){
    const button  = document.createElement("button");
    button.textContent = title;
    button.classList.add(classList);
    return button;
}
