/**
 * Convert rgb colours to hex
 * @param rgba
 * @returns {*}
 */
export function rgbaToHex(rgba) {
    const parts = rgba.substring(rgba.indexOf("(")).split(","),
        r = parseInt(parts[0].substring(1).trim(), 10),
        g = parseInt(parts[1].trim(), 10),
        b = parseInt(parts[2].trim(), 10);
    return (getHex(r, g, b));
}

/**
 * add # and format rgb colour scheme.
 * @param r
 * @param g
 * @param b
 * @returns {string}
 */
function getHex(r, g, b) {
    return "#" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
}

export function getCssVariableValue(variableName) {
    return getComputedStyle(document.documentElement).getPropertyValue(variableName);
}
