export function datasetToDraft(dataset) {
    let result = {};
    if (dataset.__definition != null) {
        for (const field of dataset.__definition.fields) {
            if (field.dataset != null) {
                result[field.name] = datasetToDraft(dataset[field.name]);
            } else if (field.datasource != null) {
                result[field.name] = dataSourceToDraft(dataset[field.name]);
            } else {
                result[field.name] = dataset[field.name];
            }
        }
    }

    return result;
}

export function dataSourceToDraft(dataSource) {
    return dataSource._data;
}