import {RemoteDatasource} from "./remote-datasource.js";
import {ResourceDataSource} from "./resource-datasource.js";

export function createDatasourceFromDefinition(schema, definition, remoteCallback) {
    const options = {
        schema: schema,
        definition: definition,
    };

    if (definition.remote != null) {
        options.remoteCallback = remoteCallback;
        return new RemoteDatasource(options);
    } else {
        return new ResourceDataSource(options);
    }
}