import {SceneGraph} from "./scene-graph.js";
import {SceneNode} from "./scene-node.js";

export class SceneGroup extends SceneNode {
    constructor(value, separator) {
        super(value);
        this.graph = new SceneGraph(this.id, separator);
        this.expanded = false;
        this.hasChildren = true;
        this.xOffset = 30;
        return this;
    }

    dispose() {
        this.expanded = null;
        this.graph.dispose();
        super.dispose();
    }

    toString() {
        return `(${this.graph.toString()})`
    }
}