import {IntDurationConverter} from "./../../../baremetal/lib/conveters/int-duration-converter.js";

describe("duration-converter-tests", () => {

    let converter = new IntDurationConverter();

    it('convert', () => {

        // Arrange/Act 1
        let value =  converter.convert(70);
        chai.assert.equal("01:10", value);        

        // Arrange/Act 1
        value =  converter.convert(423);
        chai.assert.equal("07:03", value);
    });
});
