import {PragmaIconButton} from "../../../webcomponents/pragma-icon-button/pragma-icon-button.js";

describe("pragma-icon-button-tests", () => {
    let instance;

    beforeEach(() => {
        instance = new PragmaIconButton();
    });
    
    it('ariaLabel setter', async () => {
        // Arrange
        const label = "Some text";

        // Act
        instance.ariaLabel = label;

        // Assert
        chai.assert.equal(instance._ariaLabel, label, "setter for ariaLabel - sets _ariaLabel property.");
        chai.assert.equal(instance.attributes["aria-label"].value, label, "setter for ariaLabel - sets _ariaLabel property.");
        chai.assert.equal(instance.attributes["tooltip"].value, label, "setter for ariaLabel - sets _ariaLabel property.");
    });
});