export class BaseNavigation {
    
    constructor(element) {
        // Note (AA): rename to keys instead of keycodes
        this.keyCodes = {
            leftArrow: "ArrowLeft",
            rightArrow: "ArrowRight",
            upArrow: "ArrowUp",
            downArrow: "ArrowDown",
            space: " ",
            enter: "Enter",
            escape: "Escape",
            tab: "Tab",
            home: "Home",
            end: "End"
        };
        
        this.element = element;
    }

    dispose() {
        this.keyCodes = null;
        this.element = null;
    }
}
