export function initializeContent(sourceContainer, sourceQuery, targetContainer, targetQuery) {

    const sourceElement = sourceContainer.querySelector(sourceQuery);

    if (sourceElement != null) {
        const targetElement = targetContainer.querySelector(targetQuery);
        const template = document.importNode(sourceElement, true);
        targetElement.appendChild(template.content);
        sourceElement.parentNode.removeChild(sourceElement);
    }
}