export function isMobile() {
    return navigator.userAgent.indexOf("iPhone") > 0 ||
        navigator.userAgent.indexOf("iPad") > 0 ||
        navigator.userAgent.indexOf("Android") > 0 ||
        navigator.userAgent.indexOf("webOS") > 0 ||
        navigator.userAgent.indexOf("iPod") > 0 ||
        navigator.userAgent.indexOf("BlackBerry") > 0 ||
        navigator.userAgent.indexOf("Windows Phone") > 0;
}

/**
 * This function allows you to delay animations, allowing the keyboard to disapear first without causing fps drop.
 * @param delay - in milliseconds
 * @param element - to animation
 * @param className -
 * @returns {Promise<any>}
 */
export function delayedAnimation(delay, element, className) {
    return new Promise(function(resolve) {
        if(isMobile()){
            setTimeout(() => {
                element.classList.add(className);
                resolve();
            }, delay)
        }
        else{
            element.classList.add(className);
            resolve();
        }
    });
}
