import {dateToIsoStringDate} from "./../../lib/date-helpers.js";

export class DateCell extends HTMLElement {
    static get observedAttributes() { return ["date"]; }
    
    attributeChangedCallback(name, oldValue, newValue) {
        if (name == "date") {
            if (newValue != null && newValue != "") {
                this.innerText = dateToIsoStringDate(new Date(newValue));
            }            
        }
    }
}

customElements.define('pr-date-cell', DateCell);