import { FocusBase } from "../lib/focus-base.js";

export class Focus extends FocusBase {

    focus(element) {
        if (element == null) {
            const row = this.navigation.getRowByIndex(this.index);
            if (this.navigation.isExpandable(this.index)) {
                this._focusRow(row);
            } else {
                if (row != null && row.children != null) {
                    this._focusRowItem(row.children[this.rowIndex], this.rowIndex);
                }
            }
        } else {
            this._focusRow(element);
        }
    }
}