import {HierarchicalRenderer} from "./../lib/render-hierarchical.js";

export class GridHierarchicalRenderer extends HierarchicalRenderer {
    cleanItem(element) {
        super.cleanItem(element);
        for (let child of element.children) {
            child.style.background = null;
            child.style.color = null;
        }
    }
    
    getItemFromStore(item) {
        const element =  super.getItemFromStore(item);
        const width = this.scroller.offsetWidth > this.dimensions.width? this.scroller.offsetWidth : this.dimensions.width;
        element.style.minWidth = `${width - 1}px`;
        return element;
    }
}